const abi = require('ethereumjs-abi')
const Web3 = require('web3');
const web3 = new Web3(new Web3.providers.HttpProvider("http://127.0.0.1:9545"));
const BigNumber = web3.BigNumber;
const hexToArrayBuffer = require('hex-to-array-buffer');

function  ether (n) {
  return new BigNumber(web3.toWei(n, 'ether'));
}


var res = abi.rawEncode(
  [
    'address',
    'uint256[]',
    'uint256[]',
    'uint256',
    'uint256'
  ],
  [
    '0x5e3a35df396c1324a0292309052882cc342383eb',
    [
      ether('1500').toString(),
      ether('10000').toString(),
      ether('18500').toString(),
      ether('27000').toString(),
      ether('35500').toString()
    ],
    [
      new BigNumber('1450').toString(),
      new BigNumber('1200').toString(),
      new BigNumber('1150').toString(),
      new BigNumber('1100').toString(),
      new BigNumber('1000').toString()
    ],
    ether('0.1').toString(),
    ether('100000000').toString()
  ]
)
